insert  into `pages`(`id`,`alias`,`title`,`content`,`datemodified`,`usermodified`,`menu_id`,`active`) values (1,'products-12','Products 12','<p><span style=\"color: #ff0000;\"><strong>San pham test<br /></strong></span></p>','2010-10-22 22:14:17','admin','page2',1),(2,'trang-dem-cho-tim-xac-nguoi-than','Trng m ch  tm xc ngi thn','<p class=\"Lead\">\"Th ln b&aacute;o t&igrave;m c chic xe, t&ocirc;i chy vi ra thp  hng cu khn. C &ecirc;m kh&ocirc;ng t&agrave;i n&agrave;o ng c, &agrave;nh ra b s&ocirc;ng cu mong  cho x&aacute;c ch c&ograve;n nm trong &oacute;\", th&acirc;n nh&acirc;n ca ch Phm Th C&uacute;c v&agrave; b&eacute; g&aacute;i 7  th&aacute;ng tui tr&ecirc;n chuyn xe nh mnh n&oacute;i. <br />&gt;<a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21D05/\">Ting k&ecirc;u cu tuyt vng tr&ecirc;n chic xe b l cun</a>/ <a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21E3B/\">T&igrave;m thy xe kh&aacute;ch b l cun tr&ocirc;i</a></p>\n<p class=\"Normal\">24h &ecirc;m, ngi bn thn tr&ecirc;n quc l 1A nh&igrave;n ra hai  chic t&agrave;u ang neo gi chic xe kh&aacute;ch di l&ograve;ng s&ocirc;ng Lam ang chy xit,  ch Ho&agrave;ng Th B&iacute;ch Hng n&oacute;i nh mun kh&oacute;c: \"Th ln b&aacute;o &atilde; t&igrave;m c  chic xe, t&ocirc;i chy ngay ra b s&ocirc;ng thp hng cu khn. C &ecirc;m kh&ocirc;ng t&agrave;i  n&agrave;o ng c, &agrave;nh ra b s&ocirc;ng cu mong cho x&aacute;c ch c&ograve;n nm trong &oacute;\".</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"268\" align=\"center\" bgcolor=\"#ddddff\">\n<tbody>\n<tr>\n<td>*<em>Clip</em> <a href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21E6A/page_2.asp\"><strong>Cu h xe kh&aacute;ch tr&ecirc;n s&ocirc;ng Lam</strong></a></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Ch Hng l&agrave; ngi nh&agrave; ca nn nh&acirc;n Phm Th C&uacute;c v&agrave; b&eacute;  g&aacute;i 7 th&aacute;ng tui  k Lk. Nghe tin hai m con b nn tr&ecirc;n ng v  thm qu&ecirc; Nam nh, c i gia &igrave;nh ch ngt l&ecirc;n ngt xung, b&agrave; m gi&agrave;  lm dn phi v&agrave;o vin cp cu c&ograve;n chng ch C&uacute;c c ng ng, ngn ngn&hellip;</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6A/IMG_0940a.jpg\" alt=\"\" width=\"495\" height=\"358\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Ch Hng au &aacute;u nh&igrave;n v s&ocirc;ng Lam, ni &oacute; c&oacute; ngi ch va a ch&aacute;u g&aacute;i 7 th&aacute;ng tui ca ch.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Vt ma l t Nam nh v&agrave;o Nghi Xu&acirc;n (H&agrave; Tnh), 3 &ecirc;m  lin ch Hng ra b s&ocirc;ng ng&oacute;ng tr&ocirc;ng tin tc ngi ch xu s. Mi khi  nghe mt &acirc;m thanh l nh ting c&ograve;i h&uacute; ca cnh s&aacute;t, xe cp cu&hellip;, &aacute;nh mt  ch li thp thm ch mong.</p>\n<p class=\"Normal\">Chiu 20/10, khi ngi th ln ngoi l&ecirc;n b khng nh  &atilde; t&igrave;m c chic xe b tr&ocirc;i, ch &atilde; &ocirc;m chm ly th&acirc;n nh&acirc;n c&aacute;c nn nh&acirc;n.  C &ecirc;m qua kh&ocirc;ng ng c, ch li ra b s&ocirc;ng cu khn hy vng cho x&aacute;c  s c&ograve;n  trong xe v&agrave; qu&aacute; tr&igrave;nh trc vt din ra an to&agrave;n.</p>\n<p class=\"Normal\">K hoch trc vt chic xe ngay trong &ecirc;m b hy b v&igrave;  s&oacute;ng to, gi&oacute; ln v&agrave; nc s&acirc;u, mt s th&acirc;n nh&acirc;n l&ecirc;n &ocirc;t&ocirc; v ph&ograve;ng ngh  ly sc ch ng&agrave;y mai, mt s thc trng tr&ecirc;n b s&ocirc;ng  cu nguyn.</p>\n<p class=\"Normal\">C&ugrave;ng chung t&acirc;m trng nh ch Hng, anh Nguyn Vn Th&acirc;n   Thanh H&oacute;a, ch&uacute; rut ca hai ch em  Th Phng (17 tui),  Th  Lan (15 tui) tr&ecirc;n chuyn xe nh mnh cng ch hy vng s c&ograve;n x&aacute;c ca  ch&aacute;u g&aacute;i m&igrave;nh trong xe.</p>\n<p class=\"Normal\">&ldquo;Hai ch em n&oacute; kh lm, hc ht lp 6 &atilde; phi b hc  i T&acirc;y Nguy&ecirc;n trng c&agrave; ph&ecirc;, nay hai a v  n ci ngi d&igrave; rut. Ai  ng mi ngi li phi lo &aacute;m tang chung cho hai a. Cha m n&oacute; ngt xu  khi nghe tin con b cht cha t&igrave;m thy x&aacute;c&rdquo;, anh Th&acirc;n n&oacute;i trong nc  mt.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6A/IMG_0945a.jpg\" alt=\"\" width=\"495\" height=\"362\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Anh Th&acirc;n thc trng &ecirc;m b&ecirc;n b s&ocirc;ng Lam.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">&ecirc;m qua, c ch Hng v&agrave; anh Th&acirc;n thn th nh&igrave;n ra bin  nc m&ecirc;nh m&ocirc;ng, thnh thong li cu tri mong cho n s&aacute;ng.  kh&aacute;ch  sn ni nhng th&acirc;n nh&acirc;n ang ngh ngi, nhng ngi kh&aacute;c cng chong &egrave;n  sut &ecirc;m ch tri s&aacute;ng v&agrave; cu mong cho x&aacute;c ca ngi th&acirc;n cha tr&ocirc;i khi  xe&hellip;</p>\n<p class=\"Normal\">&ecirc;m qua, trng s&aacute;ng vng vc, nc s&ocirc;ng Lam vn   ng&agrave;u, thnh thong ting s&oacute;ng nc v v&agrave;o b k&egrave; ln vi ting kh&oacute;c nc  n ca th&acirc;n nh&acirc;n nn nh&acirc;n.</p>\n<p class=\"Normal\">4h s&aacute;ng 18/10, xe kh&aacute;ch t k N&ocirc;ng ra Bc n x&atilde; Xu&acirc;n  Lam (Nghi Xu&acirc;n, H&agrave; Tnh) &atilde; b l cun lt v&agrave; tr&ocirc;i xung s&ocirc;ng Lam.  Nhiu h&agrave;nh kh&aacute;ch &atilde; p ca k&iacute;nh tho&aacute;t ra. Lc lng cu h v&agrave; ngi d&acirc;n  ch&egrave;o thuyn ra cu c 18 ngi. 20 ngi c&ograve;n li b mt t&iacute;ch.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguy&ecirc;n Khoa &ndash; Nguyn Hng</strong></p>','2010-10-22 22:21:45','admin','page1',1),(3,'doc-suc-lo-cho-mien-trung-ruot-thit','Dc sc lo cho min Trung rut tht','<p class=\"Lead\">11h tra nay, xe kh&aacute;ch b cun tr&ocirc;i ti Nghi Xu&acirc;n (H&agrave;  Tnh) &atilde; c t&agrave;u k&eacute;o l&ecirc;n v&agrave; a v&agrave;o b s&ocirc;ng Lam. Trc &oacute; 4 x&aacute;c h&agrave;nh  kh&aacute;ch &atilde; c t&igrave;m thy.<br />* Tip tc cp nht (c gi nhn F5).<br />&gt; <a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21D05/\">Ting k&ecirc;u cu tuyt vng tr&ecirc;n chic xe b l cun</a>/ <a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21E3B/\">T&igrave;m thy xe kh&aacute;ch b l cun tr&ocirc;i</a></p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/3B/a5.jpg\" alt=\"\" width=\"495\" height=\"330\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&aacute;c lc lng tp kt tr&ecirc;n s&ocirc;ng Lam. nh: <em>H&agrave; Khoa</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Trong s nn nh&acirc;n c t&igrave;m thy, mt ngi &atilde; x&aacute;c nh  danh t&iacute;nh l&agrave; t&agrave;i x inh Vn Lng. Anh n&agrave;y b mc ngay bi c&acirc;y c&aacute;ch  a im xe b tr&ocirc;i v&agrave;i trm m&eacute;t, tr&ecirc;n ngi c&ograve;n nguy&ecirc;n chic v&iacute; cha  rt nhiu giy t v&agrave; tin bc. Trong xe c cho l&agrave; c&oacute; 19 nn nh&acirc;n.</p>\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"307\" align=\"center\" bgcolor=\"#ffe1e1\">\n<tbody>\n<tr>\n<td><em>Clip</em>:<strong><a href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21E3B/page_2.asp\"> Cu h xe kh&aacute;ch tr&ecirc;n s&ocirc;ng Lam</a></strong></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">T 6h30 lc lng trc vt khong 100 ngi gm c&ocirc;ng  binh, b i bi&ecirc;n ph&ograve;ng, c&ocirc;ng an v&agrave; lc lng cu h ca 6 doanh nghip  &atilde; c&oacute; mt. Ngo&agrave;i hai t&agrave;u k&eacute;o c ln, hai x&agrave; lan ca doanh nghip, rt  nhiu cano t&igrave;m kim trc &oacute;, s&aacute;ng nay qu&acirc;n i &atilde; huy ng th&ecirc;m mt t&agrave;u  u k&eacute;o c ln phc v cho vic trc vt.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/3B/902.jpg\" border=\"1\" alt=\"\" width=\"495\" height=\"350\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Chun b trc vt.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">9h, hai chic t&agrave;u, hai chic x&agrave; lan chia th&agrave;nh hai  nh&oacute;m c&ugrave;ng tip cn v tr&iacute; chic xe. C&aacute;c th ln ngm v&ograve;i oxy v&agrave; mang  theo d&acirc;y c&aacute;p, sn s&agrave;ng nhy xung s&ocirc;ng. Theo k hoch, sau khi m&oacute;c d&acirc;y  c&aacute;p, t&agrave;u v&agrave; x&agrave; lan s n&acirc;ng xe l&ecirc;n,  xe li nc v&agrave; xe u k&eacute;o chuy&ecirc;n  dng ca qu&acirc;n i k&eacute;o v&agrave;o b.</p>\n<p class=\"Normal\">9h10, lc lng cu h vt th&ecirc;m x&aacute;c mt nn nh&acirc;n l&agrave;  anh inh Xu&acirc;n Thng. Thi th nn nh&acirc;n c a v&agrave;o l&aacute;n tp kt, ni c&oacute;  th&acirc;n nh&acirc;n ang ch sn  nhn dng.</p>\n<p class=\"Normal\">9h15 hai th ln ngoi l&ecirc;n mt nc cho bit xe kh&aacute;ch  nm ngang s&ocirc;ng, h &atilde; m&oacute;c d&acirc;y c&aacute;p v&agrave;o xe  chun b k&eacute;o l&ecirc;n. Tr&ecirc;n hai  chic t&agrave;u, ngi nh&agrave; nn nh&acirc;n ang l&agrave;m l theo phong tc a phng.</p>\n<p class=\"Normal\">9h50, hai th ln ngoi l&ecirc;n mt nc cho bit, vic  buc c&aacute;p rt kh&oacute; khn do nc xit, xe nm s&acirc;u. Tin nht l&agrave; buc v&agrave;o  b&aacute;nh xe, nhng c 8 b&aacute;nh u b v&ugrave;i s&acirc;u trong c&aacute;t. Lc lng trc vt  li hi &yacute;  b&agrave;n c&aacute;ch buc d&acirc;y c&aacute;p kh&aacute;c.</p>\n<p class=\"Normal\">Khong 10 ph&uacute;t sau, th ln th&ocirc;ng b&aacute;o &atilde; buc c c&aacute;p v&agrave;o b&aacute;nh xe, chun b n&acirc;ng xe l&ecirc;n khi v tr&iacute;.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/3B/lan.jpg\" alt=\"\" width=\"495\" height=\"353\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Th ln ang m&oacute;c d&acirc;y c&aacute;p v&agrave;o xe kh&aacute;ch gp nn.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">10h30, lc lng cu h nhn c in thoi ca ng  d&acirc;n b&aacute;o ph&aacute;t hin mt thi th tr&ecirc;n s&ocirc;ng Lam on qua phng Bn Thy,  th&agrave;nh ph Vinh (Ngh An), c&aacute;ch ni v tr&iacute; xe tai nn khong 10 km. Ng  d&acirc;n &atilde; neo x&aacute;c li  ch c quan chc nng n gii quyt.</p>\n<p class=\"Normal\">Dc b s&ocirc;ng Lam, khong 10 xung cao tc ca c&ocirc;ng an,  b i, bi&ecirc;n ph&ograve;ng li&ecirc;n tc tun tiu quanh v tr&iacute; xe gp nn  h tr.  Mt s ngi d&acirc;n ch&egrave;o thuyn nan ra sn s&agrave;ng gi&uacute;p  khi cn thit.</p>\n<p class=\"Normal\">Quc l 1A b phong ta khong mt km. Tuy nhi&ecirc;n, h&agrave;ng ngh&igrave;n ngi d&acirc;n &atilde; i v&ograve;ng l&ecirc;n n&uacute;i  chng kin.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/3B/900.jpg\" border=\"1\" alt=\"\" width=\"495\" height=\"350\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">H&agrave;ng ngh&igrave;n ngi ng tr&ecirc;n n&uacute;i theo d&otilde;i trc vt.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"normal\">Tnh H&agrave; Tnh cho bit s h tr to&agrave;n b kinh ph&iacute; kh&acirc;m  lim, gi&aacute;m nh ph&aacute;p y, v sinh y t, chun b xe a thi th nn nh&acirc;n  v qu&ecirc;. Mi gia &igrave;nh c h tr 6 triu ng.</p>\n<p class=\"Normal\">Chiu ti 20/10, sau nhiu gi ln kho s&aacute;t v&agrave; neo c&aacute;p  c nh chic xe kh&aacute;ch di l&ograve;ng s&ocirc;ng, lc lng cu h quyt nh ch  ti s&aacute;ng nay mi tin h&agrave;nh trc vt.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyn Hng - Nguy&ecirc;n Khoa</strong></p>\n<p></p>','2010-10-22 17:30:42','nclong','page3',1),(10,'test','test','<p>test</p>','2010-10-22 22:14:46','admin','0',0);

